$(document).ready(function () {
    $('.carousel').carousel({
        padding: 30,
        // fullWidth: true,
        duration: 2000,
        // noWrap: true,
        indicators: true,
    });

    $('#next').click(function () {
        $('.carousel').carousel('next');
    });

    $('#previous').click(function () {
        $('.carousel').carousel('prev');
    });

    setInterval(() => {
        $('.carousel').carousel('next');

    }, 3000);
});
